import Vditor from 'vditor';
// 1.2 引入样式
import 'vditor/dist/index.css';
import { ref, onMounted } from 'vue';

// 2. 获取DOM引用

export default {
  __name: 'Index',
  setup(__props, {
    expose: __expose
  }) {
    __expose();

    // 1.1 引入Vditor 构造函数
    const vditor = ref();

    // 3. 在组件初始化时，就创建Vditor对象，并引用
    onMounted(() => {
      vditor.value = new Vditor('vditor', {
        // height: '200px',
        width: '80vw',
        height: '85vh',
        tab: "\t",
        // "outline": {
        // 	"enable": true
        // },
        toolbar: ['emoji', 'headings', 'bold', 'italic', 'strike', '|', 'line', 'quote', 'list', 'ordered-list', 'check', 'outdent', 'indent', 'code', 'inline-code', 'insert-after', 'insert-before', 'undo', 'redo', 'upload', 'link', 'table', 'record', 'edit-mode']
      });
    });
    const __returned__ = {
      vditor,
      get Vditor() {
        return Vditor;
      },
      ref,
      onMounted
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
