import request from '@/utils/request'

export function getCommentList(articleId,pageNumber, pageSize){
	return request({
		url:'/comment/get',
		method:'get',
		params:{
			articleId,pageNumber, pageSize
		}
	})
}

export function getComment(articleId,pageNumber, pageSize){
	return request({
		url:'/comment/get',
		method:'get',
		params:{
			articleId,pageNumber, pageSize
		}
	})
}
