import { ref, onMounted, inject } from 'vue';
import { save, download, getUserInfo } from '@/api/user';
import { ElMessage, ElMessageBox } from 'element-plus';
import { useRouter } from "vue-router";
export default {
  __name: 'Index',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const router = useRouter();
    let showMessage = ref(false);
    let userInfo = ref(JSON.parse(localStorage.getItem("userInfo")));
    const imgDownload = inject('globalImgDownload');

    // let password = ref(null);
    let avatar = ref();
    //上传头像

    const headers = ref({
      "Token": localStorage.getItem("token")
    });
    const uploadImgUrl = ref("http://43.129.171.44:8081/user/upload/avatar");
    const ImageData = ref();
    // 上传之前
    onMounted(() => {
      // userInfo.value.avatar=require(userInfo.value.avatar);
      // getImageUrl();
      // downloadAvatar();
    });
    const beforeAvatarUpload = rawFile => {
      if (rawFile.type !== "image/jpeg" && rawFile.type !== "image/png" && rawFile.type !== "image/jpg") {
        ElMessage({
          showClose: true,
          message: '图片只能是JPG或PNG格式！',
          type: 'warning'
        });
        return;
      }
      if (rawFile.size / 1024 / 1024 > 5) {
        ElMessage({
          showClose: true,
          message: '图片大小不能超过5MB！',
          type: 'warning'
        });
        return;
      }
      // 一般是formData格式
      ImageData.value = new FormData();
      // 'file'具体是什么字段看项目接口文档
      ImageData.value.append('file', rawFile);
    };
    function reset() {
      ElMessageBox.confirm('确定重置吗？', {
        confirmButtonText: '确认',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        userInfo.value = JSON.parse(localStorage.getItem("userInfo"));
        ElMessage({
          type: 'success',
          message: '重置成功'
        });
      }).catch(() => {
        ElMessage({
          type: 'info',
          message: '取消'
        });
      });
    }
    // 上传成功回调
    const handleAvatarSuccess = (res, uploadFile) => {
      avatar.value = URL.createObjectURL(uploadFile.raw);
    };
    function downloadAvatar() {
      download(userInfo.value.avatar).then(res => {
        // const blob = new Blob([res.data]);
        const reader = new FileReader();
        reader.readAsDataURL(res);
        reader.onload = () => {
          avatar.value = reader.result;
        };
        // avatar.value=res;
      });
    }
    function getUser() {
      getUserInfo().then(res => {
        localStorage.setItem("userInfo", JSON.stringify(res.data));
        userInfo.value = res.data;
      });
    }
    function saveUser() {
      ElMessageBox.confirm('确定保存吗？', {
        confirmButtonText: '确认',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        let user = {
          "name": userInfo.value.name,
          "birthday": userInfo.value.birthday,
          "sex": userInfo.value.sex,
          "location": userInfo.value.location,
          "college": userInfo.value.college,
          "createTime": userInfo.value.createTime,
          "updateTime": userInfo.value.updateTime
          // "password":userInfo.value.password,
        };

        // userInfo.value.add({
        // 	"password":password
        // })

        save(user).then(res => {
          if (res.code == 1) {
            getUser();
            ElMessage({
              type: 'success',
              message: '保存成功'
            });
          } else {
            ElMessage({
              type: 'info',
              message: '保存失败'
            });
          }
        });
      }).catch(() => {
        ElMessage({
          type: 'info',
          message: '取消'
        });
      });
    }
    const __returned__ = {
      router,
      get showMessage() {
        return showMessage;
      },
      set showMessage(v) {
        showMessage = v;
      },
      get userInfo() {
        return userInfo;
      },
      set userInfo(v) {
        userInfo = v;
      },
      imgDownload,
      get avatar() {
        return avatar;
      },
      set avatar(v) {
        avatar = v;
      },
      headers,
      uploadImgUrl,
      ImageData,
      beforeAvatarUpload,
      reset,
      handleAvatarSuccess,
      downloadAvatar,
      getUser,
      saveUser,
      ref,
      onMounted,
      inject,
      get save() {
        return save;
      },
      get download() {
        return download;
      },
      get getUserInfo() {
        return getUserInfo;
      },
      get ElMessage() {
        return ElMessage;
      },
      get ElMessageBox() {
        return ElMessageBox;
      },
      get useRouter() {
        return useRouter;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
