import { getAdminUserInfo, download } from '@/api/user.js';
import { ref, onMounted, inject } from 'vue';
import Vditor from 'vditor';
// 1.2 引入样式
import 'vditor/dist/index.css';
import { useRoute } from 'vue-router';
import { getArticleOne } from '@/api/article.js';
export default {
  __name: 'Index',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const imgDownload = inject('globalImgDownload');
    let managed = ref(false);
    const vditor = ref();
    let articleId = 1;
    let adminInfo = ref({
      "name": null,
      "birthday": null,
      "avatar": null,
      "sex": null,
      "location": null,
      "college": null
    });
    let article = ref({
      "id": null,
      "userId": "1",
      "title": "",
      "content": "",
      "published": null,
      "likeNum": null,
      "collectNum": null,
      "viewNum": null,
      "commentNum": null,
      "createTime": null,
      "updateTime": null
    });
    // let adminInfoShow=ref();
    let avatar = ref();
    onMounted(() => {
      if (articleId != null) getArticle();
      getAdmin();
    });

    //获取文章
    function getArticle() {
      if (articleId != null) getArticleOne(articleId).then(res => {
        article.value = res.data.article;
        const vditorSpan = document.getElementById("vditor");
        Vditor.preview(vditorSpan, article.value.content, {
          hljs: {
            style: "manni"
          }
          // theme:{
          // 	current:"wechat"
          // },
        });
      });
    }
    function downloadAvatar() {
      download(adminInfo.value.avatar).then(res => {
        // const blob = new Blob([res.data]);
        const reader = new FileReader();
        reader.readAsDataURL(res);
        reader.onload = () => {
          avatar.value = reader.result;
        };
        // avatar.value=res;
      });
    }
    function getAdmin() {
      getAdminUserInfo().then(res => {
        adminInfo.value = res.data;
        // adminName=adminInfo.value.name
        downloadAvatar();
      });
    }
    const __returned__ = {
      imgDownload,
      get managed() {
        return managed;
      },
      set managed(v) {
        managed = v;
      },
      vditor,
      get articleId() {
        return articleId;
      },
      set articleId(v) {
        articleId = v;
      },
      get adminInfo() {
        return adminInfo;
      },
      set adminInfo(v) {
        adminInfo = v;
      },
      get article() {
        return article;
      },
      set article(v) {
        article = v;
      },
      get avatar() {
        return avatar;
      },
      set avatar(v) {
        avatar = v;
      },
      getArticle,
      downloadAvatar,
      getAdmin,
      get getAdminUserInfo() {
        return getAdminUserInfo;
      },
      get download() {
        return download;
      },
      ref,
      onMounted,
      inject,
      get Vditor() {
        return Vditor;
      },
      get useRoute() {
        return useRoute;
      },
      get getArticleOne() {
        return getArticleOne;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
