import { onMounted, ref } from 'vue';
import { getArticles, getArticlePrivate } from "@/api/article.js";
import { deleteArticle } from "@/api/article.js";
import { ElMessage, ElMessageBox } from 'element-plus';
export default {
  __name: 'Index',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    let drafted = ref(false);
    let blogs = ref([]);
    let articleStatus = ref();
    let newTitle = null;
    let articleListElement = {
      "pageNumber": 1,
      "pageSize": 4,
      "userId": 1,
      "tagId": null,
      "title": null,
      "published": null,
      "order": 1
    };
    onMounted(() => {
      getList();
    });
    function handlePublished(newPublished) {
      articleListElement.published = newPublished;
      getList();
    }
    function handleSearch() {
      articleListElement.title = newTitle;
      getList();
    }
    function handlePageChange(newPage) {
      articleListElement.pageNumber = newPage; // 更新pageNumber的值
      getList(); // 调用getList方法
    }
    function getList() {
      // getArticleList(pageNumber, pageSize, userId, tagId, title, published, order)
      // 	.then(res=>{
      // 		blogs.value=res.data;

      // 	})

      getArticlePrivate(articleListElement).then(res => {
        blogs.value = res.data;
      }).catch;
    }
    function deleteA(articleId) {
      ElMessageBox.confirm('确定删除吗？', 'Warning', {
        confirmButtonText: '确认',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        deleteArticle(articleId).then(res => {
          getList();
          if (res.code == 1) {
            ElMessage({
              type: 'success',
              message: '删除成功'
            });
          } else {
            ElMessage({
              type: 'info',
              message: '删除失败'
            });
          }
        });
      }).catch(() => {
        ElMessage({
          type: 'info',
          message: '取消'
        });
      });
    }
    function openDrafted() {
      drafted.value = true;
    }
    function closeDrafted() {
      drafted.value = false;
    }
    const __returned__ = {
      get drafted() {
        return drafted;
      },
      set drafted(v) {
        drafted = v;
      },
      get blogs() {
        return blogs;
      },
      set blogs(v) {
        blogs = v;
      },
      get articleStatus() {
        return articleStatus;
      },
      set articleStatus(v) {
        articleStatus = v;
      },
      get newTitle() {
        return newTitle;
      },
      set newTitle(v) {
        newTitle = v;
      },
      get articleListElement() {
        return articleListElement;
      },
      set articleListElement(v) {
        articleListElement = v;
      },
      handlePublished,
      handleSearch,
      handlePageChange,
      getList,
      deleteA,
      openDrafted,
      closeDrafted,
      onMounted,
      ref,
      get getArticles() {
        return getArticles;
      },
      get getArticlePrivate() {
        return getArticlePrivate;
      },
      get deleteArticle() {
        return deleteArticle;
      },
      get ElMessage() {
        return ElMessage;
      },
      get ElMessageBox() {
        return ElMessageBox;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
