import VanillaTilt from 'vanilla-tilt';
import { onMounted, reactive, ref } from "vue";
import { FormInstance, FormRules } from 'element-plus';
export default {
  __name: 'Index',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    let isLogin = ref(true);
    let backgroundImg = ref(require("@/assets/imgs/3.png"));
    let color = ref("blak");
    onMounted(() => {
      // VanillaTilt.init(document.querySelector(".card"), {
      // 	max: 50,
      // 	speed: 400
      // });

      //It also supports NodeList
      VanillaTilt.init(document.querySelectorAll(".login-left"));
    });
    function openLogin() {
      isLogin.value = true;
      backgroundImg.value = require("@/assets/imgs/3.png");
    }
    function closeLogin() {
      isLogin.value = false;
      backgroundImg.value = require("@/assets/imgs/10.jpg");
    }

    //使用ref定义dom对象
    const ruleForms = ref(null);

    //使用reactive定于数据
    const state = reactive({
      selectObj: [{
        value: "1",
        label: "项目1"
      }, {
        value: "2",
        label: "项目2"
      }, {
        value: "3",
        label: "项目3"
      }, {
        value: "4",
        label: "项目4"
      }],
      //定义提交的数据
      formInfoObj: {
        titleVal: "",
        urlVal: "",
        selectVal: ""
      },
      rules: {
        //定义验证规则，有两种方式：
        //1.使用ele原生的验证规则。
        titleVal: [{
          required: true,
          message: "请输入标题",
          trigger: "blur"
        }],
        //2.通过引用整体的验证规则的js来进行验证。
        urlVal: [{
          required: true,
          validator: globalValidate.requiredFunction,
          trigger: "blur"
        }],
        selectVal: [{
          required: true,
          message: "此项为必填",
          trigger: "blur"
        }]
      }
    });

    //定义提交按钮函数
    const submitButton = () => {
      //通过ref的值触发验证
      ruleForms.value.validate(valid => {
        if (valid) {
          console.log("通过");
          console.log(state.formInfoObj);
          //触发成功验证表单，调用接口；
        } else {
          console.log("未通过");
        }
      });
    };

    // const ruleFormRef = ref()

    const __returned__ = {
      get isLogin() {
        return isLogin;
      },
      set isLogin(v) {
        isLogin = v;
      },
      get backgroundImg() {
        return backgroundImg;
      },
      set backgroundImg(v) {
        backgroundImg = v;
      },
      get color() {
        return color;
      },
      set color(v) {
        color = v;
      },
      openLogin,
      closeLogin,
      ruleForms,
      state,
      submitButton,
      get VanillaTilt() {
        return VanillaTilt;
      },
      onMounted,
      reactive,
      ref,
      get FormInstance() {
        return FormInstance;
      },
      get FormRules() {
        return FormRules;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
